                                Double impLavori = l.getQuadroEconomicoConcessioni().getImpLavori() != null ? l.getQuadroEconomicoConcessioni().getImpLavori() : 0D;
                                Double impServizi = l.getQuadroEconomicoConcessioni().getImpServizi() != null ? l.getQuadroEconomicoConcessioni().getImpServizi() : 0D;
